import React from 'react'
import './assets/css/base/base.css'
import Home from './paginas/Home'
import Sobre from './paginas/Sobre'


function App() {
//1.2 crio uma funcao router, será arrow function, dentro dela colocarei onde estou na pág
//window.location.pathname é o nome exato do caminho
//exec o Router no return para chamar a funcao
//condicao: se acessar sobre ele entra no sobre, senao entra no home

  const Router =() =>{
    const location = window.location.pathname 
    if (location === '/sobre'){
      return <Sobre />
    } else {
      return <Home />
    }

  }


  return (
  <>

    {Router()}
  </>
  )
}

export default App
